import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';

const LandingPage = ({ onNavigate }) => {
    const { login, isAuthenticated } = useAuth();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        
        if (!email || !password) {
            setError('Please fill in all fields');
            return;
        }
        
        try {
            setLoading(true);
            await login(email, password);
            onNavigate('home');
        } catch (err) {
            setError(err.message || 'Failed to sign in');
        } finally {
            setLoading(false);
        }
    };

    const handleGoogleLogin = () => {
        console.log('Google login initiated');
    };

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    return (
        <div className="flex flex-row h-screen">
            {/* Left Side - Dark Background */}
            <div className="w-1/2 bg-gray-900 text-white flex items-center justify-center p-8 md:p-12">
                <div className="max-w-lg">
                    <div className="flex items-center mb-12">
                        <div className="bg-yellow-500 p-2 rounded-md">
                            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-6 h-6">
                                <path fillRule="evenodd" d="M1.5 4.5a3 3 0 013-3h1.372c.86 0 1.61.586 1.819 1.42l1.105 4.423a1.875 1.875 0 01-.694 1.955l-1.293.97c-.135.101-.164.249-.126.352a11.285 11.285 0 006.697 6.697c.103.038.25.009.352-.126l.97-1.293a1.875 1.875 0 011.955-.694l4.423 1.105c.834.209 1.42.959 1.42 1.82V19.5a3 3 0 01-3 3h-2.25C8.552 22.5 1.5 15.448 1.5 6.75V4.5z" clipRule="evenodd" />
                            </svg>
                        </div>
                        <span className="text-white font-bold text-xl ml-2">SignEd.</span>
                        <span className="text-blue-500 ml-2">•</span>
                    </div>
                    
                    <h1 className="text-5xl sm:text-6xl font-light text-white mb-6 animate-fade-in">
                        Welcome.
                    </h1>
                    <p className="text-2xl sm:text-4xl text-gray-400 font-light leading-relaxed">
                        Start your journey<br />
                        with our sign language<br />
                        calculator system!
                    </p>
                    
                    <div className="mt-12 space-y-4">
                        {isAuthenticated ? (
                            <button 
                                onClick={() => onNavigate('home')}
                                className="w-full px-6 py-3 bg-blue-600 hover:bg-blue-700 rounded-lg font-medium text-white transition-all duration-300 transform hover:scale-105"
                            >
                                Go to Dashboard
                            </button>
                        ) : (
                            <button 
                                onClick={() => onNavigate('login')}
                                className="w-full px-6 py-3 bg-transparent hover:bg-blue-800/20 border border-blue-500 text-blue-400 rounded-lg font-medium transition-colors duration-300"
                            >
                                Sign In
                            </button>
                        )}
                    </div>
                </div>
            </div>

            {/* Right Side - White Background with Sign In Form */}
            <div className="w-1/2 bg-white flex items-center justify-center p-8 md:p-12">
                <div className="w-full max-w-md">
                    <h2 className="text-3xl font-bold text-gray-800 mb-8 text-center">
                        Sign In
                    </h2>
                    
                    {error && (
                        <div className="mb-6 p-3 bg-red-50 border border-red-300 text-red-800 rounded-lg">
                            <p>{error}</p>
                        </div>
                    )}
                    
                    <form onSubmit={handleLogin} className="space-y-6">
                        <div>
                            <label htmlFor="email" className="block text-gray-700 mb-2 font-medium">
                                Email  
                            </label>
                            <input
                                id="email"
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                placeholder="balamia@gmail.com"
                                required
                            />
                        </div>

                        <div>
                            <label htmlFor="password" className="block text-gray-700 mb-2 font-medium">
                                Password  
                            </label>
                            <div className="relative">
                                <input
                                    id="password"
                                    type={showPassword ? "text" : "password"}
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                    placeholder="Enter your password"
                                    required
                                />
                                <button
                                    type="button"
                                    onClick={togglePasswordVisibility}
                                    className="absolute inset-y-0 right-0 flex items-center pr-4"
                                >
                                    {showPassword ? (
                                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="h-5 w-5 text-gray-400">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                                        </svg>
                                    ) : (
                                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="h-5 w-5 text-gray-400">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                                        </svg>
                                    )}
                                </button>
                            </div>
                        </div>

                        <div className="flex justify-end">
                            <button 
                                type="button" 
                                className="text-blue-500 hover:text-blue-600 text-sm font-medium"
                            >
                                Forgot password?
                            </button>
                        </div>

                        <button
                            type="submit"
                            disabled={loading}
                            className="w-full bg-blue-500 hover:bg-blue-600 text-white py-3 rounded-lg font-medium transition-colors duration-300"
                        >
                            {loading ? 'Signing in...' : 'Sign In'}
                        </button>
                    </form>

                    <div className="relative flex items-center justify-center my-6">
                        <div className="absolute border-t border-gray-300 w-full"></div>
                        <div className="bg-white px-4 relative text-sm text-gray-500">or</div>
                    </div>

                    <button
                        onClick={handleGoogleLogin}
                        className="w-full flex items-center justify-center gap-3 bg-white hover:bg-gray-50 text-gray-700 py-3 rounded-lg font-medium transition-colors duration-300 mb-8 border border-gray-300"
                    >
                        <svg className="w-5 h-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 488 512">
                            <path fill="#4285F4" d="M488 261.8C488 403.3 391.1 504 248 504 110.8 504 0 393.2 0 256S110.8 8 248 8c66.8 0 123 24.5 166.3 64.9l-67.5 64.9C258.5 52.6 94.3 116.6 94.3 256c0 86.5 69.1 156.6 153.7 156.6 98.2 0 135-70.4 140.8-106.9H248v-85.3h236.1c2.3 12.7 3.9 24.9 3.9 41.4z"/>
                        </svg>
                        Continue with Google
                    </button>
                </div>
            </div>
        </div>
    );
};

export default LandingPage;
