import React, { useState, useRef, useCallback, useEffect } from 'react';

// --- Import Sign Language Images ---
// This method allows React to bundle the images with the app.
// Supports both PNG and JPG formats

// Helper function to import images with fallback
const importImage = (imageName) => {
    try {
        // Try PNG first
        return require(`./assets/signs/${imageName}.png`);
    } catch (e) {
        try {
            // Fallback to JPG
            return require(`./assets/signs/${imageName}.jpg`);
        } catch (e2) {
            // If neither exists, return null or a placeholder
            console.warn(`Image not found: ${imageName} (tried both .png and .jpg)`);
            return null;
        }
    }
};

// Import all images with dual format support
const imgNum0 = importImage('number_0');
const imgNum1 = importImage('number_1');
const imgNum2 = importImage('number_2');
const imgNum3 = importImage('number_3');
const imgNum4 = importImage('number_4');
const imgNum5 = importImage('number_5');
const imgNum6 = importImage('number_6');
const imgNum7 = importImage('number_7');
const imgNum8 = importImage('number_8');
const imgNum9 = importImage('number_9');
const imgTambah = importImage('operator_tambah');
const imgKurang = importImage('operator_kurang');
const imgKali = importImage('operator_kali');
const imgBagi = importImage('operator_bagi');
const imgStart = importImage('command_start');
const imgUndefined = importImage('command_undefined');


// --- Main App Component: Manages navigation between pages ---
function App() {
    const [page, setPage] = useState('landing'); // 'landing', 'home', 'help', 'calculator'

    const navigateTo = (pageName) => {
        setPage(pageName);
    };

    const renderPage = () => {
        switch (page) {
            case 'home':
                return <HomePage onNavigate={navigateTo} />;
            case 'help':
                return <HelpPage onNavigate={navigateTo} />;
            case 'calculator':
                return <CalculatorPage onNavigate={navigateTo} />;
            case 'landing':
            default:
                return <LandingPage onNavigate={navigateTo} />;
        }
    };

    return (
        <div className="bg-gray-900 text-white min-h-screen w-full flex flex-col items-center justify-center font-sans p-4">
            {renderPage()}
        </div>
    );
}

// --- Page Components ---

// 1. Landing Page Component
function LandingPage({ onNavigate }) {
    return (
        <div className="w-full max-w-md mx-auto text-center bg-gray-800 p-8 rounded-2xl shadow-lg animate-fade-in">
            <h1 className="text-4xl font-bold mb-2 text-blue-400">
                YOLOv11 Sign Language Calculator
            </h1>
            <p className="text-lg text-gray-300 mb-8">
                An interactive way to learn and practice arithmetic using sign language.
            </p>
            <div className="flex flex-col items-center space-y-4">
                 <button 
                    onClick={() => onNavigate('home')}
                    className="w-full px-8 py-3 bg-blue-600 hover:bg-blue-700 rounded-lg font-semibold transition-all duration-300 transform hover:scale-105"
                >
                    Login as Guest
                </button>
                <p className="text-xs text-gray-500">
                    No account needed. Just start learning!
                </p>
            </div>
        </div>
    );
}

// 2. Home Page Component
function HomePage({ onNavigate }) {
    return (
        <div className="w-full max-w-md mx-auto text-center bg-gray-800 p-8 rounded-2xl shadow-lg animate-fade-in">
            <h1 className="text-3xl font-bold mb-4">Welcome!</h1>
            <p className="text-gray-300 mb-8">
                Choose an option to get started.
            </p>
            <div className="flex flex-col space-y-4">
                <button 
                    onClick={() => onNavigate('calculator')}
                    className="w-full px-8 py-4 bg-green-600 hover:bg-green-700 rounded-lg font-semibold text-lg transition-all duration-300 transform hover:scale-105"
                >
                    Start Calculator
                </button>
                <button 
                    onClick={() => onNavigate('help')}
                    className="w-full px-8 py-3 bg-gray-600 hover:bg-gray-700 rounded-lg font-semibold transition-all duration-300 transform hover:scale-105"
                >
                    Help (View Signs)
                </button>
                 <button 
                    onClick={() => onNavigate('landing')}
                    className="mt-4 text-sm text-gray-400 hover:text-white transition-colors"
                >
                    Logout
                </button>
            </div>
        </div>
    );
}

// 3. Help Page Component
function HelpPage({ onNavigate }) {
    const signs = [
        { name: 'Number 0', img: imgNum0 },
        { name: 'Number 1', img: imgNum1 },
        { name: 'Number 2', img: imgNum2 },
        { name: 'Number 3', img: imgNum3 },
        { name: 'Number 4', img: imgNum4 },
        { name: 'Number 5', img: imgNum5 },
        { name: 'Number 6', img: imgNum6 },
        { name: 'Number 7', img: imgNum7 },
        { name: 'Number 8', img: imgNum8 },
        { name: 'Number 9', img: imgNum9 },
        { name: 'Tambah (+)', img: imgTambah },
        { name: 'Kurang (-)', img: imgKurang },
        { name: 'Kali (x)', img: imgKali },
        { name: 'Bagi (/)', img: imgBagi },
        { name: 'Start', img: imgStart },
        { name: 'Undefined', img: imgUndefined },
    ].filter(sign => sign.img !== null); // Filter out signs with missing images

    return (
        <div className="w-full max-w-4xl mx-auto bg-gray-800 p-8 rounded-2xl shadow-lg animate-fade-in">
            <h1 className="text-3xl font-bold mb-6 text-center">Sign Language Guide</h1>
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-6">
                {signs.map((sign) => (
                    <div key={sign.name} className="flex flex-col items-center bg-gray-700 p-4 rounded-lg">
                        {sign.img ? (
                            <img 
                                src={sign.img} 
                                alt={sign.name} 
                                className="w-full h-auto rounded-md mb-2 object-cover aspect-square"
                                onError={(e) => {
                                    e.target.style.display = 'none';
                                    e.target.nextSibling.style.display = 'flex';
                                }}
                            />
                        ) : null}
                        <div 
                            className="w-full aspect-square bg-gray-600 rounded-md mb-2 flex items-center justify-center text-gray-400 text-sm hidden"
                            style={{display: sign.img ? 'none' : 'flex'}}
                        >
                            Image not found
                        </div>
                        <p className="font-semibold text-center">{sign.name}</p>
                    </div>
                ))}
            </div>
            <div className="text-center mt-8">
                <button 
                    onClick={() => onNavigate('home')}
                    className="px-8 py-3 bg-blue-600 hover:bg-blue-700 rounded-lg font-semibold transition-all duration-300"
                >
                    Back to Home
                </button>
            </div>
        </div>
    );
}

// 4. Calculator Page Component
function CalculatorPage({ onNavigate }) {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const [isCameraOn, setIsCameraOn] = useState(false);
    const [analysisResult, setAnalysisResult] = useState({
        detected_class: null,
        state: {
            current_state: "WAIT_FIRST_NUM",
            number_1: null,
            operator: null,
            number_2: null,
            result: null,
        }
    });
    const [statusMessage, setStatusMessage] = useState("Click 'Start Camera' to begin.");
    const [isAnalyzing, setIsAnalyzing] = useState(false);

    // Function to start the camera
    const startCamera = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ video: true });
            if (videoRef.current) {
                videoRef.current.srcObject = stream;
                setIsCameraOn(true);
                setStatusMessage("Please show the first number (0-9).");
            }
        } catch (err) {
            console.error("Error accessing camera:", err);
            setStatusMessage("Camera access denied. Please allow camera permissions.");
        }
    };

    // Function to stop the camera
    const stopCamera = () => {
        if (videoRef.current && videoRef.current.srcObject) {
            videoRef.current.srcObject.getTracks().forEach(track => track.stop());
            setIsCameraOn(false);
            setStatusMessage("Click 'Start Camera' to begin.");
        }
    };

    // Function to manually reset the calculator
    const resetCalculator = () => {
        setAnalysisResult({
            detected_class: null,
            state: {
                current_state: "WAIT_FIRST_NUM",
                number_1: null,
                operator: null,
                number_2: null,
                result: null,
            }
        });
        setStatusMessage("Calculator reset. Please show the first number.");
    };

    // Function to capture a frame and send for analysis
    const runAnalysis = useCallback(async () => {
        if (isAnalyzing || !videoRef.current || !videoRef.current.srcObject) return;
        setIsAnalyzing(true);

        const canvas = canvasRef.current;
        const video = videoRef.current;
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        const context = canvas.getContext('2d');
        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        canvas.toBlob(async (blob) => {
            const formData = new FormData();
            formData.append('file', blob, 'frame.jpg');

            try {
                const response = await fetch('http://localhost:8000/api/detect', {
                    method: 'POST',
                    body: formData,
                });
                const data = await response.json();
                setAnalysisResult(data);
                
                // Handle reset when Start is detected
                if (data.detected_class === 'Start') {
                    // Reset to initial state after a brief delay to show the reset message
                    setTimeout(() => {
                        setAnalysisResult({
                            detected_class: null,
                            state: {
                                current_state: "WAIT_FIRST_NUM",
                                number_1: null,
                                operator: null,
                                number_2: null,
                                result: null,
                            }
                        });
                    }, 1500); // 1.5 second delay to show reset message
                }
            } catch (error) {
                console.error("Analysis failed:", error);
                setStatusMessage("Analysis failed. Is the backend running?");
            } finally {
                setIsAnalyzing(false);
            }
        }, 'image/jpeg');
    }, [isAnalyzing]);

    // Helper to update status message based on state
    const updateStatusMessage = useCallback(() => {
        const { state, detected_class } = analysisResult;
        if (!state) return;

        if (detected_class === 'Start') {
            setStatusMessage("System reset! Starting fresh calculation...");
            return;
        }
        if (detected_class === 'Undefined') {
            setStatusMessage("Gesture not recognized. Please try again.");
            return;
        }

        switch (state.current_state) {
            case "WAIT_FIRST_NUM":
                setStatusMessage("Please show the first number (0-9).");
                break;
            case "WAIT_OPERATOR":
                setStatusMessage(`Got it: ${state.number_1}. Now show an operator.`);
                break;
            case "WAIT_SECOND_NUM":
                setStatusMessage(`OK: ${state.number_1} ${state.operator}. Now show the second number.`);
                break;
            case "SHOWING_RESULT":
                setStatusMessage(`Calculation complete! Result: ${state.result}. Show 'Start' to reset.`);
                break;
            default:
                setStatusMessage("Ready to start.");
        }
    }, [analysisResult]);

    useEffect(() => {
        updateStatusMessage();
    }, [analysisResult, updateStatusMessage]);

    // Helper to get dynamic class for UI boxes
    const getBoxClass = (boxName) => {
        const currentState = analysisResult.state?.current_state;
        let isActive = false;
        if (boxName === 'number_1' && currentState === 'WAIT_FIRST_NUM') isActive = true;
        if (boxName === 'operator' && currentState === 'WAIT_OPERATOR') isActive = true;
        if (boxName === 'number_2' && currentState === 'WAIT_SECOND_NUM') isActive = true;
        return `p-4 rounded-lg transition-all text-center ${isActive ? 'active-box' : 'bg-gray-800'}`;
    };
    
    // Cleanup camera on component unmount
    useEffect(() => {
        return () => {
            // eslint-disable-next-line react-hooks/exhaustive-deps
            const video = videoRef.current;
            if (video && video.srcObject) {
                video.srcObject.getTracks().forEach(track => track.stop());
            }
        };
    }, []);

    return (
        <div className="w-full max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8 animate-fade-in">
            {/* Left Panel: Camera and Controls */}
            <div className="flex flex-col items-center bg-gray-800 p-6 rounded-2xl shadow-lg">
                <div className="w-full flex justify-between items-center mb-4">
                    <h1 className="text-2xl font-bold">YOLOv11 Calculator</h1>
                    <button onClick={() => onNavigate('home')} className="text-sm text-blue-400 hover:text-blue-300">
                        &larr; Back to Home
                    </button>
                </div>
                <div className="w-full aspect-video bg-black rounded-lg overflow-hidden mb-4">
                    <video ref={videoRef} autoPlay playsInline className="w-full h-full object-cover"></video>
                    <canvas ref={canvasRef} className="hidden"></canvas>
                </div>
                <div className="flex flex-wrap gap-2 justify-center">
                    <button onClick={isCameraOn ? stopCamera : startCamera} className="px-6 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg font-semibold transition-colors">
                        {isCameraOn ? 'Stop Camera' : 'Start Camera'}
                    </button>
                    <button onClick={runAnalysis} disabled={!isCameraOn || isAnalyzing} className="px-6 py-2 bg-green-600 hover:bg-green-700 rounded-lg font-semibold transition-colors disabled:bg-gray-500">
                        {isAnalyzing ? 'Analyzing...' : 'Run Analysis'}
                    </button>
                    <button onClick={resetCalculator} className="px-6 py-2 bg-red-600 hover:bg-red-700 rounded-lg font-semibold transition-colors">
                        Reset
                    </button>
                </div>
            </div>

            {/* Right Panel: Results */}
            <div className="flex flex-col justify-center bg-gray-800 p-6 rounded-2xl shadow-lg">
                <h2 className="text-2xl font-bold mb-4 text-center">Calculation</h2>
                <div className="status-message bg-gray-700 p-3 rounded-lg mb-6">
                    <p className="text-lg text-center font-mono">{statusMessage}</p>
                </div>
                <div className="calculation-display flex items-center justify-center space-x-2 md:space-x-4 text-3xl md:text-5xl font-mono mb-6">
                    <span className={getBoxClass('number_1')}>{analysisResult.state?.number_1 ?? '_'}</span>
                    <span className={getBoxClass('operator')}>{analysisResult.state?.operator ?? '_'}</span>
                    <span className={getBoxClass('number_2')}>{analysisResult.state?.number_2 ?? '_'}</span>
                    <span className="mx-2">=</span>
                    <span className="p-4 rounded-lg bg-gray-800 text-center">{analysisResult.state?.result ?? '_'}</span>
                </div>
                <div className="bg-gray-700 p-3 rounded-lg text-center">
                    <p>Last Detected: <span className="font-bold text-green-400">{analysisResult.detected_class ?? 'None'}</span></p>
                    <p>Current State: <span className="font-bold text-yellow-400">{analysisResult.state?.current_state ?? 'N/A'}</span></p>
                </div>
            </div>
        </div>
    );
}

export default App;
