import React, { useState, useRef, useCallback, useEffect } from 'react';

const CalculatorPage = ({ onNavigate }) => {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const analysisIntervalRef = useRef(null);
    const analysisTimeoutRef = useRef(null);
    
    const [isCameraOn, setIsCameraOn] = useState(false);
    const [analysisResult, setAnalysisResult] = useState({
        detected_class: null,
        state: {
            current_state: "WAIT_FIRST_NUM",
            number_1: null,
            operator: null,
            number_2: null,
            result: null,
        }
    });
    const [statusMessage, setStatusMessage] = useState("Click 'Start Camera' to begin.");
    const [isAnalyzing, setIsAnalyzing] = useState(false);
    const [analysisCountdown, setAnalysisCountdown] = useState(0);

    // Function to start the camera
    const startCamera = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ video: true });
            if (videoRef.current) {
                videoRef.current.srcObject = stream;
                setIsCameraOn(true);
                setStatusMessage("Please show the first number (0-9).");
            }
        } catch (err) {
            console.error("Error accessing camera:", err);
            setStatusMessage("Camera access denied. Please allow camera permissions.");
        }
    };

    // Function to stop the camera
    const stopCamera = () => {
        // Clear any ongoing analysis
        clearAnalysisTimers();
        
        if (videoRef.current && videoRef.current.srcObject) {
            videoRef.current.srcObject.getTracks().forEach(track => track.stop());
            setIsCameraOn(false);
            setStatusMessage("Click 'Start Camera' to begin.");
        }
    };

    // Function to clear analysis timers
    const clearAnalysisTimers = () => {
        if (analysisIntervalRef.current) {
            clearInterval(analysisIntervalRef.current);
            analysisIntervalRef.current = null;
        }
        if (analysisTimeoutRef.current) {
            clearTimeout(analysisTimeoutRef.current);
            analysisTimeoutRef.current = null;
        }
        setIsAnalyzing(false);
        setAnalysisCountdown(0);
    };

    // Function to manually reset the calculator
    const resetCalculator = () => {
        // Clear any ongoing analysis
        clearAnalysisTimers();
        
        // Clear cache and reset state
        setAnalysisResult({
            detected_class: null,
            state: {
                current_state: "WAIT_FIRST_NUM",
                number_1: null,
                operator: null,
                number_2: null,
                result: null,
            }
        });
        setStatusMessage("Calculator reset. Please show the first number.");
        
        // Clear any cached data (if you have localStorage or sessionStorage)
        // Since we can't use localStorage in artifacts, we just reset the state
        console.log("Cache cleared and calculator reset");
    };

    // Function to capture a single frame and analyze
    const captureAndAnalyze = useCallback(async () => {
        if (!videoRef.current || !videoRef.current.srcObject) return null;

        const canvas = canvasRef.current;
        const video = videoRef.current;
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        const context = canvas.getContext('2d');
        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        return new Promise((resolve) => {
            canvas.toBlob(async (blob) => {
                const formData = new FormData();
                formData.append('file', blob, 'frame.jpg');

                try {
                    const response = await fetch('http://localhost:8001/api/detect', {
                        method: 'POST',
                        body: formData,
                    });
                    const data = await response.json();
                    resolve(data);
                } catch (error) {
                    console.error("Analysis failed:", error);
                    resolve(null);
                }
            }, 'image/jpeg');
        });
    }, []);

    // Function to run analysis with 3-second auto-detection
    const runAnalysis = useCallback(async () => {
        if (isAnalyzing || !videoRef.current || !videoRef.current.srcObject) return;
        
        setIsAnalyzing(true);
        setAnalysisCountdown(3);
        setStatusMessage("Analyzing... Show your gesture now!");

        // Countdown timer
        const countdownInterval = setInterval(() => {
            setAnalysisCountdown(prev => {
                if (prev <= 1) {
                    clearInterval(countdownInterval);
                    return 0;
                }
                return prev - 1;
            });
        }, 1000);

        // Analysis interval - check every 500ms for gestures
        analysisIntervalRef.current = setInterval(async () => {
            const result = await captureAndAnalyze();
            if (result) {
                // Check if a valid gesture is detected (not 'Undefined' or null)
                if (result.detected_class && result.detected_class !== 'Undefined') {
                    // Gesture detected! Stop analysis and update result
                    clearAnalysisTimers();
                    clearInterval(countdownInterval);
                    
                    setAnalysisResult(result);
                    
                    // Handle reset when Start is detected
                    if (result.detected_class === 'Start') {
                        setTimeout(() => {
                            setAnalysisResult({
                                detected_class: null,
                                state: {
                                    current_state: "WAIT_FIRST_NUM",
                                    number_1: null,
                                    operator: null,
                                    number_2: null,
                                    result: null,
                                }
                            });
                            console.log("Cache cleared after Start gesture");
                        }, 1500);
                    }
                    
                    setStatusMessage(`Gesture detected: ${result.detected_class}`);
                    return;
                }
            }
        }, 500); // Check every 500ms

        // Timeout after 3 seconds if no gesture detected
        analysisTimeoutRef.current = setTimeout(() => {
            clearAnalysisTimers();
            clearInterval(countdownInterval);
            setStatusMessage("No gesture detected within 3 seconds. Try again.");
        }, 3000);

    }, [isAnalyzing, captureAndAnalyze]);

    // Helper to update status message based on state
    const updateStatusMessage = useCallback(() => {
        // Don't update status if currently analyzing
        if (isAnalyzing) return;
        
        const { state, detected_class } = analysisResult;
        if (!state) return;

        if (detected_class === 'Start') {
            setStatusMessage("System reset! Starting fresh calculation...");
            return;
        }
        if (detected_class === 'Undefined') {
            setStatusMessage("Gesture not recognized. Please try again.");
            return;
        }

        switch (state.current_state) {
            case "WAIT_FIRST_NUM":
                setStatusMessage("Please show the first number (0-9).");
                break;
            case "WAIT_OPERATOR":
                setStatusMessage(`Got it: ${state.number_1}. Now show an operator.`);
                break;
            case "WAIT_SECOND_NUM":
                setStatusMessage(`OK: ${state.number_1} ${state.operator}. Now show the second number.`);
                break;
            case "SHOWING_RESULT":
                setStatusMessage(`Calculation complete! Result: ${state.result}. Show 'Start' to reset.`);
                break;
            default:
                setStatusMessage("Ready to start.");
        }
    }, [analysisResult, isAnalyzing]);

    useEffect(() => {
        updateStatusMessage();
    }, [analysisResult, updateStatusMessage]);

    // Helper to get dynamic class for UI boxes
    const getBoxClass = (boxName) => {
        const currentState = analysisResult.state?.current_state;
        let isActive = false;
        if (boxName === 'number_1' && currentState === 'WAIT_FIRST_NUM') isActive = true;
        if (boxName === 'operator' && currentState === 'WAIT_OPERATOR') isActive = true;
        if (boxName === 'number_2' && currentState === 'WAIT_SECOND_NUM') isActive = true;
        return `p-4 rounded-lg transition-all text-center ${isActive ? 'active-box' : 'bg-gray-800'}`;
    };
    
    // Cleanup camera and timers on component unmount
    useEffect(() => {
        const videoElement = videoRef.current;
        
        return () => {
            clearAnalysisTimers();
            if (videoElement && videoElement.srcObject) {
                videoElement.srcObject.getTracks().forEach(track => track.stop());
            }
        };
    }, []);

    return (
        <div className="w-full max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8 animate-fade-in">
            {/* Left Panel: Camera and Controls */}
            <div className="flex flex-col items-center bg-gray-800 p-6 rounded-2xl shadow-lg">
                <div className="w-full flex justify-between items-center mb-4">
                    <h1 className="text-2xl font-bold">YOLOv11 Calculator</h1>
                    <button onClick={() => onNavigate('home')} className="text-sm text-blue-400 hover:text-blue-300">
                        &larr; Back to Home
                    </button>
                </div>
                <div className="w-full aspect-video bg-black rounded-lg overflow-hidden mb-4 relative">
                    <video ref={videoRef} autoPlay playsInline className="w-full h-full object-cover"></video>
                    <canvas ref={canvasRef} className="hidden"></canvas>
                    
                    {/* Analysis countdown overlay */}
                    {isAnalyzing && analysisCountdown > 0 && (
                        <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-50">
                            <div className="text-white text-6xl font-bold animate-pulse">
                                {analysisCountdown}
                            </div>
                        </div>
                    )}
                </div>
                
                <div className="flex flex-wrap gap-2 justify-center">
                    <button 
                        onClick={isCameraOn ? stopCamera : startCamera} 
                        className="px-6 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg font-semibold transition-colors"
                    >
                        {isCameraOn ? 'Stop Camera' : 'Start Camera'}
                    </button>
                    <button 
                        onClick={runAnalysis} 
                        disabled={!isCameraOn || isAnalyzing} 
                        className="px-6 py-2 bg-green-600 hover:bg-green-700 rounded-lg font-semibold transition-colors disabled:bg-gray-500"
                    >
                        {isAnalyzing ? `Analyzing... (${analysisCountdown}s)` : 'Run Analysis (3s)'}
                    </button>
                    <button 
                        onClick={resetCalculator} 
                        className="px-6 py-2 bg-red-600 hover:bg-red-700 rounded-lg font-semibold transition-colors"
                    >
                        Reset & Clear Cache
                    </button>
                </div>
                
                {/* Analysis status indicator */}
                {isAnalyzing && (
                    <div className="mt-4 w-full">
                        <div className="bg-gray-700 rounded-full h-2">
                            <div 
                                className="bg-green-500 h-2 rounded-full transition-all duration-1000"
                                style={{ width: `${((3 - analysisCountdown) / 3) * 100}%` }}
                            ></div>
                        </div>
                        <p className="text-center text-sm text-gray-400 mt-2">
                            Auto-detecting gestures... {analysisCountdown}s remaining
                        </p>
                    </div>
                )}
            </div>

            {/* Right Panel: Results */}
            <div className="flex flex-col justify-center bg-gray-800 p-6 rounded-2xl shadow-lg">
                <h2 className="text-2xl font-bold mb-4 text-center">Calculation</h2>
                <div className="status-message bg-gray-700 p-3 rounded-lg mb-6">
                    <p className="text-lg text-center font-mono">{statusMessage}</p>
                </div>
                <div className="calculation-display flex items-center justify-center space-x-2 md:space-x-4 text-3xl md:text-5xl font-mono mb-6">
                    <span className={getBoxClass('number_1')}>{analysisResult.state?.number_1 ?? '_'}</span>
                    <span className={getBoxClass('operator')}>{analysisResult.state?.operator ?? '_'}</span>
                    <span className={getBoxClass('number_2')}>{analysisResult.state?.number_2 ?? '_'}</span>
                    <span className="mx-2">=</span>
                    <span className="p-4 rounded-lg bg-gray-800 text-center">{analysisResult.state?.result ?? '_'}</span>
                </div>
                <div className="bg-gray-700 p-3 rounded-lg text-center">
                    <p>Last Detected: <span className="font-bold text-green-400">{analysisResult.detected_class ?? 'None'}</span></p>
                    <p>Current State: <span className="font-bold text-yellow-400">{analysisResult.state?.current_state ?? 'N/A'}</span></p>
                </div>
            </div>
        </div>
    );
};

export default CalculatorPage;
