import React, { useState, useRef, useEffect } from 'react';

// --- Main Application Component ---
const App = () => {
    // Refs to hold references to DOM elements
    const videoRef = useRef(null);

    // State to manage the application's data and UI
    const [webcamStatus, setWebcamStatus] = useState('Initializing webcam...');
    const [analysisStatus, setAnalysisStatus] = useState('Awaiting input...');
    const [detectedObject, setDetectedObject] = useState('---');
    const [confidence, setConfidence] = useState(0);
    const [isAnalyzing, setIsAnalyzing] = useState(false);

    // --- Webcam Activation Logic ---
    // This effect runs once when the component mounts to set up the webcam.
    useEffect(() => {
        async function setupWebcam() {
            // Check if the browser supports mediaDevices API
            if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
                try {
                    // Request access to the user's webcam
                    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
                    if (videoRef.current) {
                        videoRef.current.srcObject = stream;
                        // The 'onloadedmetadata' event ensures we have video dimensions before playing
                        videoRef.current.onloadedmetadata = () => {
                            videoRef.current.play();
                            setWebcamStatus("Webcam active. ✅");
                        };
                    }
                } catch (err) {
                    console.error("Error accessing webcam: ", err);
                    setWebcamStatus("Could not access webcam. Please grant permission. ❌");
                }
            } else {
                setWebcamStatus("Webcam not supported by this browser. ❌");
            }
        }
        setupWebcam();
    }, []); // The empty dependency array ensures this effect runs only once.

    // --- Mockup Result Simulation ---
    const handleMockAnalysis = () => {
        setIsAnalyzing(true);
        setAnalysisStatus("Analyzing...");

        // Simulate a network request or a machine learning model processing time
        setTimeout(() => {
            const mockObjects = ["Laptop", "Coffee Mug", "Keyboard", "Mouse", "Smartphone", "Book"];
            const randomObject = mockObjects[Math.floor(Math.random() * mockObjects.length)];
            const randomConfidence = Math.floor(Math.random() * (100 - 75 + 1) + 75); // Random score between 75-100%

            // Update the state with the new mock data
            setDetectedObject(randomObject);
            setConfidence(randomConfidence);
            setAnalysisStatus("Analysis Complete");
            setIsAnalyzing(false);
        }, 1500); // Simulate a 1.5-second analysis delay
    };

    // --- JSX for Rendering the UI ---
    return (
        <div className="bg-gray-100 dark:bg-gray-900 font-sans text-gray-900 dark:text-gray-100 min-h-screen">
            <main className="flex flex-col lg:flex-row h-screen">
                
                {/* Left Column: Webcam Feed */}
                <section className="w-full lg:w-1/2 p-4 sm:p-6 md:p-8 bg-gray-200 dark:bg-gray-800 flex flex-col items-center justify-center">
                    <div className="w-full max-w-2xl text-center">
                        <h2 className="text-2xl sm:text-3xl font-bold mb-4">Webcam Feed</h2>
                        <div className="w-full bg-black rounded-lg overflow-hidden shadow-2xl aspect-video">
                            {/* The webcam video will be streamed into this video tag */}
                            <video ref={videoRef} className="w-full h-full object-cover" autoPlay playsInline muted></video>
                        </div>
                        <p className="mt-4 text-sm text-gray-600 dark:text-gray-400">{webcamStatus}</p>
                    </div>
                </section>

                {/* Right Column: Results */}
                <section className="w-full lg:w-1/2 p-4 sm:p-6 md:p-8 flex flex-col items-center justify-center">
                    <div className="w-full max-w-2xl">
                        <h2 className="text-3xl sm:text-4xl font-bold mb-6 text-center">Analysis Results</h2>
                        
                        <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg space-y-6">
                            {/* Detected Object Display */}
                            <div>
                                <h3 className="font-semibold text-lg text-gray-500 dark:text-gray-400">DETECTED OBJECT</h3>
                                <p className="text-2xl font-bold text-indigo-600 dark:text-indigo-400" id="detected-object">{detectedObject}</p>
                            </div>
                            
                            {/* Confidence Score Display */}
                            <div>
                                <h3 className="font-semibold text-lg text-gray-500 dark:text-gray-400">CONFIDENCE</h3>
                                <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-4 mt-2">
                                    <div 
                                        className="bg-indigo-600 h-4 rounded-full transition-all duration-500 ease-out" 
                                        style={{ width: `${confidence}%` }}>
                                    </div>
                                </div>
                                <p className="text-right text-lg font-medium mt-1">{confidence}%</p>
                            </div>

                            {/* Status Display */}
                            <div>
                                <h3 className="font-semibold text-lg text-gray-500 dark:text-gray-400">STATUS</h3>
                                <p className={`font-bold text-xl ${isAnalyzing ? 'text-yellow-500' : 'text-green-500'}`}>
                                    {analysisStatus}
                                </p>
                            </div>
                        </div>
                        
                        {/* Analysis Button */}
                        <button 
                            onClick={handleMockAnalysis}
                            disabled={isAnalyzing}
                            className="mt-8 w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-4 rounded-lg transition-all duration-300 shadow-md hover:shadow-lg disabled:bg-gray-400 disabled:cursor-not-allowed"
                        >
                            {isAnalyzing ? 'Analyzing...' : 'Run Mock Analysis'}
                        </button>
                    </div>
                </section>

            </main>
        </div>
    );
};

export default App;
