import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';

const Register = ({ navigateTo }) => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const { register } = useAuth();

  const validateEmail = (email) => {
    const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Reset messages
    setError('');
    setSuccess('');
    
    // Validate input fields
    if (!username || !email || !password) {
      setError('All fields are required');
      return;
    }
    
    if (!validateEmail(email)) {
      setError('Please enter a valid email address');
      return;
    }
    
    if (password.length < 6) {
      setError('Password must be at least 6 characters long');
      return;
    }

    try {
      setLoading(true);
      await register(username, email, password);
      setSuccess('Account created successfully! You can now sign in.');
      
      // Clear form after successful registration
      setUsername('');
      setEmail('');
      setPassword('');
      
      // Redirect to login after 2 seconds
      setTimeout(() => {
        navigateTo('login');
      }, 2000);
      
    } catch (error) {
      setError(error.message || 'Failed to create account. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <div className="min-h-screen bg-[#111827]">
      {/* Logo - Fixed at top left corner using fixed positioning */}
      <div className="fixed top-6 left-6">
        <div className="flex items-center">
          <div className="bg-yellow-500 p-2 rounded-md">
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className="w-6 h-6">
              <path fillRule="evenodd" d="M1.5 4.5a3 3 0 013-3h1.372c.86 0 1.61.586 1.819 1.42l1.105 4.423a1.875 1.875 0 01-.694 1.955l-1.293.97c-.135.101-.164.249-.126.352a11.285 11.285 0 006.697 6.697c.103.038.25.009.352-.126l.97-1.293a1.875 1.875 0 011.955-.694l4.423 1.105c.834.209 1.42.959 1.42 1.82V19.5a3 3 0 01-3 3h-2.25C8.552 22.5 1.5 15.448 1.5 6.75V4.5z" clipRule="evenodd" />
            </svg>
          </div>
          <span className="text-white font-bold text-xl ml-2">SignEd.</span>
          <span className="text-blue-500 ml-2">â€¢</span>
        </div>
      </div>

      {/* Main Content - Centered on page */}
      <div className="min-h-screen flex items-center justify-center">
        <div 
          className="bg-[#1a212e] rounded-[20px] shadow-xl"
          style={{
            width: '540px',
            height: '500px', // Slightly increased height to accommodate username field
            padding: '40px 60px'
          }}
        >
          <div className="h-full flex flex-col">
            {/* Header */}
            <div className="text-center mb-5">
              <h2 className="text-2xl font-semibold text-white">
                Create an account
              </h2>
            </div>
            
            {/* Content */}
            <div className="flex-1 flex flex-col">
              {error && (
                <div className="bg-red-900/30 border border-red-500 text-red-300 px-3 py-2 rounded-md mb-3 text-sm" role="alert">
                  <span className="block sm:inline">{error}</span>
                </div>
              )}
              
              {success && (
                <div className="bg-green-900/30 border border-green-500 text-green-300 px-3 py-2 rounded-md mb-3 text-sm" role="alert">
                  <span className="block sm:inline">{success}</span>
                </div>
              )}
              
              {/* Social Sign-in Buttons */}
              <div className="flex gap-3 mb-4">
                <button 
                  className="flex-1 flex items-center justify-center gap-2 py-2 px-3 bg-white hover:bg-gray-100 text-gray-800 rounded-md transition-colors text-sm font-medium"
                  onClick={() => alert('Google sign in not implemented')}
                >
                  <svg className="w-4 h-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 488 512">
                    <path fill="#4285F4" d="M488 261.8C488 403.3 391.1 504 248 504 110.8 504 0 393.2 0 256S110.8 8 248 8c66.8 0 123 24.5 166.3 64.9l-67.5 64.9C258.5 52.6 94.3 116.6 94.3 256c0 86.5 69.1 156.6 153.7 156.6 98.2 0 135-70.4 140.8-106.9H248v-85.3h236.1c2.3 12.7 3.9 24.9 3.9 41.4z"/>
                  </svg>
                  Google
                </button>
                <button 
                  className="flex-1 flex items-center justify-center gap-2 py-2 px-3 bg-white hover:bg-gray-100 text-gray-800 rounded-md transition-colors text-sm font-medium"
                  onClick={() => alert('Facebook sign in not implemented')}
                >
                  <svg className="w-4 h-4" fill="#1877F2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 320 512">
                    <path d="M279.14 288l14.22-92.66h-88.91v-60.13c0-25.35 12.42-50.06 52.24-50.06h40.42V6.26S260.43 0 225.36 0c-73.22 0-121.08 44.38-121.08 124.72v70.62H22.89V288h81.39v224h100.17V288z"/>
                  </svg>
                  Facebook
                </button>
              </div>
              
              {/* Divider */}
              <div className="relative flex items-center justify-center mb-4">
                <div className="absolute border-t border-gray-600 w-full"></div>
                <div className="relative bg-[#1a212e] px-3 text-sm text-gray-400">Or</div>
              </div>
              
              {/* Form */}
              <form className="flex-1 flex flex-col" onSubmit={handleSubmit}>
                <div className="space-y-3 mb-4">
                  {/* Username field - added to match database schema */}
                  <div>
                    <label htmlFor="username" className="block text-sm font-medium text-gray-400 mb-1">
                      Username
                    </label>
                    <input
                      id="username"
                      name="username"
                      type="text"
                      autoComplete="username"
                      required
                      className="appearance-none bg-white relative block w-full px-3 py-2.5 border border-gray-200 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      placeholder="username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                    />
                  </div>
                  
                  <div>
                    <label htmlFor="email" className="block text-sm font-medium text-gray-400 mb-1">
                      Email
                    </label>
                    <input
                      id="email"
                      name="email"
                      type="email"
                      autoComplete="email"
                      required
                      className="appearance-none bg-white relative block w-full px-3 py-2.5 border border-gray-200 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      placeholder="example@gmail.com"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </div>
                  
                  <div className="relative">
                    <div className="flex justify-between items-center mb-1">
                      <label htmlFor="password" className="block text-sm font-medium text-gray-400">
                        Password
                      </label>
                      <button 
                        type="button"
                        className="text-sm text-blue-500 hover:text-blue-400 transition-colors"
                        onClick={() => navigateTo('forgot-password')}
                      >
                        Forgot ?
                      </button>
                    </div>
                    <div className="relative">
                      <input
                        id="password"
                        name="password"
                        type={showPassword ? "text" : "password"}
                        autoComplete="new-password"
                        required
                        className="appearance-none bg-white relative block w-full px-3 py-2.5 border border-gray-200 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        placeholder="Enter your password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                      />
                      <button
                        type="button"
                        className="absolute inset-y-0 right-0 pr-3 flex items-center"
                        onClick={togglePasswordVisibility}
                      >
                        {showPassword ? (
                          <svg className="h-4 w-4 text-gray-400 hover:text-gray-600 transition-colors" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M3.28 2.22a.75.75 0 00-1.06 1.06l14.5 14.5a.75.75 0 101.06-1.06l-1.745-1.745a10.029 10.029 0 003.3-4.38 1.651 1.651 0 000-1.185A10.004 10.004 0 009.999 3a9.956 9.956 0 00-4.744 1.194L3.28 2.22zM7.752 6.69l1.092 1.092a2.5 2.5 0 013.374 3.373l1.091 1.092a4 4 0 00-5.557-5.557z" clipRule="evenodd" />
                            <path d="M10.748 13.93l2.523 2.523a9.987 9.987 0 01-3.27.547c-4.258 0-7.894-2.66-9.337-6.41a1.651 1.651 0 010-1.186A10.007 10.007 0 012.839 6.02L6.07 9.252a4 4 0 004.678 4.678z" />
                          </svg>
                        ) : (
                          <svg className="h-4 w-4 text-gray-400 hover:text-gray-600 transition-colors" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                            <path d="M10 12a2 2 0 100-4 2 2 0 000 4z" />
                            <path fillRule="evenodd" d="M.458 10C1.732 5.943 5.522 3 10 3s8.268 2.943 9.542 7c-1.274 4.057-5.064 7-9.542 7S1.732 14.057.458 10zM14 10a4 4 0 11-8 0 4 4 0 018 0z" clipRule="evenodd" />
                          </svg>
                        )}
                      </button>
                    </div>
                  </div>
                </div>

                <button
                  type="submit"
                  disabled={loading}
                  className="w-full flex justify-center py-2.5 px-4 border border-transparent rounded-md shadow-sm text-white text-sm font-medium bg-blue-500 hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 mb-4"
                >
                  {loading ? 'Creating account...' : 'Create account'}
                </button>
                
                {/* Sign In Link */}
                <div className="text-center">
                  <p className="text-gray-400 text-sm">
                    Already Have An Account?{' '}
                    <button 
                      onClick={() => navigateTo('login')}
                      className="font-medium text-blue-500 hover:text-blue-400 transition-colors"
                      type="button"
                    >
                      Log In
                    </button>
                  </p>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
