import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import logoImage from '../assets/img/SignEd_logo.png';

const Header = ({ onNavigate }) => {
    const { logout } = useAuth();
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
    const [scrolled, setScrolled] = useState(false);
    
    // Handle scroll effect
    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 20) {
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        };
        
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);
    
    const handleLogout = () => {
        logout();
        onNavigate('landing');
    };

    return (
        <nav className={`sticky top-0 z-50 w-full px-4 sm:px-6 md:px-8 lg:px-12 py-3 sm:py-4 md:py-5 transition-all duration-300 ${scrolled ? 'bg-[#0f1725] shadow-lg' : 'bg-transparent'}`}>
            {/* Desktop Menu */}
            <div className="container mx-auto flex justify-between items-center">
                <div className="flex items-center">
                    <img src={logoImage} alt="SignEd Logo" className="h-7 sm:h-8 md:h-10 mr-2" />
                    <span className="text-base sm:text-lg md:text-xl font-bold text-yellow-400">SignEd.</span>
                    
                    {/* Desktop Navigation Links */}
                    <div className="hidden md:flex ml-6 lg:ml-12 xl:ml-16 space-x-6 lg:space-x-8 xl:space-x-12">
                        <button 
                            onClick={() => onNavigate('help')} 
                            className="text-white hover:text-yellow-300 transition-colors text-sm lg:text-base xl:text-lg"
                        >
                            Panduan
                        </button>
                        <button 
                            className="text-white hover:text-yellow-300 transition-colors text-sm lg:text-base xl:text-lg"
                        >
                            Tentang
                        </button>
                    </div>
                </div>
                
                {/* Desktop Logout Button */}
                <div className="hidden md:block">
                    <button 
                        onClick={handleLogout}
                        className="px-4 sm:px-6 py-2 text-white hover:text-yellow-300 transition-colors text-base lg:text-lg"
                    >
                        Logout
                    </button>
                </div>
                
                {/* Mobile Menu Button */}
                <button 
                    className="md:hidden text-white focus:outline-none"
                    onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
                >
                    {mobileMenuOpen ? (
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg>
                    ) : (
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                        </svg>
                    )}
                </button>
            </div>
            
            {/* Mobile Menu */}
            {mobileMenuOpen && (
                <div className="md:hidden mt-4 py-4 border-t border-gray-700/30">
                    <div className="flex flex-col space-y-4">
                        <button 
                            onClick={() => {
                                onNavigate('help');
                                setMobileMenuOpen(false);
                            }} 
                            className="text-white hover:text-yellow-300 transition-colors text-lg"
                        >
                            Panduan
                        </button>
                        <button 
                            onClick={() => setMobileMenuOpen(false)}
                            className="text-white hover:text-yellow-300 transition-colors text-lg"
                        >
                            Tentang
                        </button>
                        <button 
                            onClick={() => {
                                handleLogout();
                                setMobileMenuOpen(false);
                            }}
                            className="text-white hover:text-yellow-300 transition-colors text-lg"
                        >
                            Logout
                        </button>
                    </div>
                </div>
            )}
        </nav>
    );
};

export default Header;
