import React from 'react';
import { useAuth } from '../contexts/AuthContext';

const LandingPage = ({ onNavigate }) => {
    const { isAuthenticated } = useAuth();

    return (
        <div className="w-full max-w-md mx-auto text-center bg-gray-800 p-8 rounded-2xl shadow-lg animate-fade-in">
            <h1 className="text-4xl font-bold mb-2 text-blue-400">
                YOLOv11 Sign Language Calculator
            </h1>
            <p className="text-lg text-gray-300 mb-8">
                An interactive way to learn and practice arithmetic using sign language.
            </p>
            <div className="flex flex-col items-center space-y-4">
                {isAuthenticated ? (
                    <button 
                        onClick={() => onNavigate('home')}
                        className="w-full px-8 py-3 bg-blue-600 hover:bg-blue-700 rounded-lg font-semibold transition-all duration-300 transform hover:scale-105"
                    >
                        Continue to App
                    </button>
                ) : (
                    <>
                        <button 
                            onClick={() => onNavigate('login')}
                            className="w-full px-8 py-3 bg-blue-600 hover:bg-blue-700 rounded-lg font-semibold transition-all duration-300 transform hover:scale-105"
                        >
                            Sign In
                        </button>
                        <button 
                            onClick={() => onNavigate('register')}
                            className="w-full px-8 py-3 bg-green-600 hover:bg-green-700 rounded-lg font-semibold transition-all duration-300 transform hover:scale-105"
                        >
                            Create Account
                        </button>
                        <button 
                            onClick={() => onNavigate('home')}
                            className="text-gray-400 hover:text-white text-sm mt-2"
                        >
                            Continue as Guest
                        </button>
                    </>
                )}
                <p className="text-xs text-gray-500 mt-4">
                    Create an account to save your progress!
                </p>
            </div>
        </div>
    );
};

export default LandingPage;
